<node CREATED="1224986889041" ID="Freemind_Link_1994096339" MODIFIED="1224986895021" TEXT="decides to become a villain?">
<node CREATED="1224987575632" ID="Freemind_Link_1040327771" MODIFIED="1224987580011" TEXT="other villains don&apos;t appreciate the mockery"/>
<node CREATED="1224987710594" ID="Freemind_Link_1643072919" MODIFIED="1224987715430" TEXT="serious, or deliberately bad?"/>
<node CREATED="1224987715938" ID="Freemind_Link_232388355" MODIFIED="1224987720990" TEXT="first business model is to be serious"/>
<node CREATED="1224987722162" ID="Freemind_Link_977895644" MODIFIED="1224987733149" TEXT="later business model is to have non-heros pay for the experience"/>
</node>
<node CREATED="1224987824302" ID="Freemind_Link_1944367414" MODIFIED="1224987826914" TEXT="needs a sidekick?"/>
<node CREATED="1224987829534" ID="Freemind_Link_224351599" MODIFIED="1224987834049" TEXT="PLOT:  quest for purpose in live"/>
<node CREATED="1224987834438" ID="Freemind_Link_226095010" MODIFIED="1224987842801" TEXT="can kick major ass, but just always leaves out key detail"/>
